export class ListagemCategoriasComponent {
    constructor(categoriasService, dialog) {
        this.categoriasService = categoriasService;
        this.dialog = dialog;
        this.categorias = new MatTableDataSource();
        this.autoCompleteInput = new FormControl();
        this.opcoesCategorias = [];
    }
    ngOnInit() {
        this.categoriasService.PegarTodos().subscribe(resultado => {
            resultado.forEach(categoria => {
                this.opcoesCategorias.push(categoria.nome);
            });
            this.categorias.data = resultado;
        });
        this.displayedColumns = this.ExibirColunas();
        this.nomesCategorias = this.autoCompleteInput.valueChanges.pipe(startWith(''), map(nome => this.FiltrarNomes(nome)));
    }
    ExibirColunas() {
        return ['nome', 'icone', 'tipo', 'acoes'];
    }
    AbrirDialog(categoriaId, nome) {
        this.dialog.open(DialogExclusaoCategoriasComponent, {
            data: {
                categoriaId: categoriaId,
                nome: nome
            },
        })
            .afterClosed()
            .subscribe(resultado => {
            if (resultado === true) {
                debugger;
                this.categoriasService.PegarTodos().subscribe(dados => {
                    this.categorias.data = dados;
                });
                this.displayedColumns = this.ExibirColunas();
            }
        });
    }
    FiltrarNomes(nome) {
        if (nome.trim().length >= 4) {
            this.categoriasService.FiltrarCategorias(nome.toLocaleLowerCase()).subscribe(resultado => {
                this.categorias.data = resultado;
            });
        }
        else {
            if (nome === '') {
                this.categoriasService.PegarTodos().subscribe(resultado => {
                    this.categorias.data = resultado;
                });
            }
        }
        return this.opcoesCategorias.filter(categoria => categoria.toLocaleLowerCase().includes(nome.toLocaleLowerCase()));
